import React, { useState, useEffect, useRef } from 'react'
import Navbar from './Components/Navbar'
import Field from './Components/Field'
import Autocomplete from 'react-autocomplete'

import SwapVerticalCircleOutlinedIcon from '@material-ui/icons/SwapVerticalCircleOutlined'

import './App.css'

function App () {
  const [location, setLocation] = useState({
    from: '',
    to: ''
  })
  const [suggestions, setSuggestions] = useState([
    'Botanical Garden',
    'Pari Chowk',
    'SNU'
  ])

  const fromValue = useRef(null)
  const toValue = useRef(null)

  useEffect(() => {}, [location])

  function handleChange (evt) {
    evt.preventDefault()
    const value = evt.target.value
    setLocation({
      ...location,
      [evt.target.name]: value
    })
  }

  const onClickValueChange = event => {
    const temp = location.from
    location.from = location.to
    location.to = temp
    fromValue.current.value = location.to
    toValue.current.value = location.from
  }

  return (
    <div className='app'>
      <Navbar />
      <form action=''>
        <div className='app__form'>
          <label className='input'>
            <input
              ref={fromValue}
              className='input__field'
              type='text'
              placeholder=' '
              name='from'
              id='from'
              value={location.from}
              onChange={handleChange}
            />

            <span className='input__label'>From</span>
          </label>
          <label className='input'>
            <input
              ref={toValue}
              className='input__field'
              type='text'
              placeholder=' '
              name='to'
              id='to'
              value={location.to}
              onChange={handleChange}
            />

            <span className='input__label'>To</span>
          </label>
          <div className='app__verticalButton' onClick={onClickValueChange}>
            <SwapVerticalCircleOutlinedIcon fontSize='large' />
          </div>
        </div>
      </form>

      <Autocomplete
        getItemValue={item => item.label}
        items={[{ label: 'Botanical' }, { label: 'banana' }, { label: 'pear' }]}
        renderItem={(item, isHighlighted) => (
          <div style={{ background: isHighlighted ? 'lightgray' : 'white' }}>
            {item.label}
          </div>
        )}
        value={location}
        onChange={e => (location = e.target.value)}
        onSelect={val => (location = val)}
      />
    </div>
  )
}

export default App
