import React, { useState, useEffect, useRef } from 'react'
import Navbar from './Components/Navbar'
import Field from './Components/Field'

import SwapVerticalCircleOutlinedIcon from '@material-ui/icons/SwapVerticalCircleOutlined'

import './App.css'

function App () {
  const [location, setLocation] = useState({
    from: '',
    to: ''
  })
  const [suggestions, setSuggestions] = useState([
    'Botanical Garden',
    'Pari Chowk',
    'SNU'
  ])

  const fromValue = useRef(null)
  const toValue = useRef(null)

  useEffect(() => {}, [location])

  function handleChange (evt) {
    evt.preventDefault()
    const value = evt.target.value
    setLocation({
      ...location,
      [evt.target.name]: value
    })
  }

  const onClickValueChange = event => {
    const temp = location.from
    location.from = location.to
    location.to = temp
    fromValue.current.value = location.to
    toValue.current.value = location.from
  }

  return (
    <div className='app'>
      <Navbar />
      <form action=''>
        <div className='app__form'>
          <div className='app__verticalButton' onClick={onClickValueChange}>
            <SwapVerticalCircleOutlinedIcon fontSize='large' />
          </div>
        </div>
      </form>
    </div>
  )
}

export default App
