import React, { useState, useEffect, useRef } from 'react'
import Navbar from './Components/Navbar'
import Field from './Components/Field'
import TextField from '@mui/material/TextField'
import Autocomplete from '@mui/material/Autocomplete'

import SwapVerticalCircleOutlinedIcon from '@material-ui/icons/SwapVerticalCircleOutlined'

import './App.css'

const options = ['Botanical Garden', 'Pari Chowk', 'SNU']

function App () {
  const [location, setLocation] = useState({
    from: '',
    to: ''
  })
  const [suggestions, setSuggestions] = useState('')
  const fromValue = useRef(null)
  const toValue = useRef(null)

  useEffect(() => {
    console.log(location.from)
  }, [location])

  function handleChange (evt) {
    evt.preventDefault()
    const value = evt.target.value
    setLocation({
      ...location,
      [evt.target.name]: value
    })
  }

  const onClickValueChange = event => {
    const temp = location.from
    location.from = location.to
    location.to = temp
    fromValue.current.value = location.to
    toValue.current.value = location.from
  }

  return (
    <div className='app'>
      <Navbar />
      <form action=''>
        <div className='app__form'>
          <Autocomplete
            value={suggestions}
            onChange={(event, newValue) => {
              setSuggestions(newValue)
            }}
            inputValue={location.from}
            onInputChange={(event, newInputValue) => {
              setLocation({
                ...location,
                from: newInputValue
              })
              console.log(location.from)
            }}
            id='controllable-states-demo'
            options={options}
            sx={{ width: 300 }}
            renderInput={params => <TextField {...params} label='From' />}
          />

          <div className='app__verticalButton' onClick={onClickValueChange}>
            <SwapVerticalCircleOutlinedIcon fontSize='large' />
          </div>
        </div>
      </form>
    </div>
  )
}

export default App
