import React, { useState, useEffect, useRef } from 'react'
import SwapVerticalCircleOutlinedIcon from '@material-ui/icons/SwapVerticalCircleOutlined'
import InputLabel from '@mui/material/InputLabel'
// import option from '@mui/material/Option'
import FormControl from '@mui/material/FormControl'
import Select from '@mui/material/Select'
import NativeSelect from '@mui/material/NativeSelect'

import './css/Field.css'

export default function ControllableStates ({ options, type }) {
  const [location, setLocation] = useState({
    from: '',
    to: ''
  })

  const handleChange = event => {
    const value = event.target.value
    setLocation({
      ...location,
      from: value
    })
  }

  return (
    <div className='field'>
      <form action=''>
        <div className='app__form'>
          <FormControl className='field__form'>
            <InputLabel id='demo-simple-select-label'>Country</InputLabel>
            <Select
              native
              className='field__select'
              value={location.from}
              label='From'
              onChange={handleChange}
            >
              {options.map((option, key) => (
                <option key={key} id={key} value={option}>
                  {option}
                </option>
              ))}
            </Select>
          </FormControl>
        </div>
      </form>
    </div>
  )
}
