import React, { useState, useEffect, useRef } from 'react'
import Navbar from './Components/Navbar'
import Field from './Components/Field'

import SwapVerticalCircleOutlinedIcon from '@material-ui/icons/SwapVerticalCircleOutlined'

import './App.css'

const options = ['Botanical Garden', 'Pari Chowk', 'SNU']

function App () {
  const [location, setLocation] = useState({
    from: '',
    to: ''
  })
  const [suggestions, setSuggestions] = useState([])
  const fromValue = useRef(null)
  const toValue = useRef(null)

  useEffect(() => {
    // console.log(suggestions)
  }, [suggestions])

  function handleChange (evt) {
    evt.preventDefault()
    const value = evt.target.value
    let suggest = []
    if (value.length > 0) {
      const regex = new RegExp(`^${value}`, 'i')
      suggest = options.sort().filter(v => v.match(regex))
      setSuggestions(suggest)
    } else {
      setSuggestions([])
    }

    setLocation({
      ...location,
      [evt.target.name]: value
    })
  }

  function suggestionSelected (value) {
    setLocation({
      ...location,
      from: value
    })

    setSuggestions([])
  }

  function renderSuggestions () {
    if (suggestions.length === 0) {
      return (
        <ul>
          {options.map((item, key) => (
            <li onClick={() => suggestionSelected(item)}>{item}</li>
          ))}
        </ul>
      )
    } else {
      return (
        <ul>
          {suggestions.map((item, key) => (
            <li onClick={() => suggestionSelected(item)}>{item}</li>
          ))}
        </ul>
      )
    }
  }

  const onClickValueChange = event => {
    const temp = location.from
    location.from = location.to
    location.to = temp
    fromValue.current.value = location.to
    toValue.current.value = location.from
  }

  return (
    <div className='app'>
      <Navbar />
    </div>
  )
}

export default App
