import React, { useState, useEffect, useRef } from 'react'
import SwapVerticalCircleOutlinedIcon from '@material-ui/icons/SwapVerticalCircleOutlined'
import { FormControl, option, Select, InputLabel } from '@material-ui/core'

import './css/Field.css'

export default function Field ({ options, type }) {
  const [location, setLocation] = useState({
    from: '',
    to: ''
  })

  useEffect(() => {
    // console.log(location.from)
  }, [location])

  const handleChange = event => {
    const value = event.target.value
    setLocation({
      ...location,
      from: value
    })
  }

  return (
    <div className='field'>
      <form action=''>
        <div className='app__form'>
          <FormControl className='field__form'>
            <InputLabel id='demo-simple-select-label'>From</InputLabel>
            <Select
              native
              className='field__select'
              renderValue={value => {
                return <div>{value}</div>
              }}
              value={location.from}
              label='From'
              onChange={handleChange}
            >
              {options.map((option, key) => (
                <option primaryText={option} key={key} id={key} value={option}>
                  {option}
                </option>
              ))}
            </Select>
          </FormControl>
        </div>
      </form>
    </div>
  )
}
