import React, { useState, useEffect, useRef } from 'react'
import Navbar from './Components/Navbar'
import Field from './Components/Field'

import SwapVerticalCircleOutlinedIcon from '@material-ui/icons/SwapVerticalCircleOutlined'

import './App.css'

function App () {
  const [location, setLocation] = useState({
    from: '',
    to: ''
  })
  const [suggestions, setSuggestions] = useState([
    'Botanical Garden',
    'Pari Chowk',
    'SNU'
  ])
  const [filteredSuggestions, setFilteredSuggestions] = useState([])
  const [activeSuggestionIndex, setActiveSuggestionIndex] = useState(0)
  const [showSuggestions, setShowSuggestions] = useState(false)

  const fromValue = useRef(null)
  const toValue = useRef(null)

  useEffect(() => {}, [location])

  const SuggestionsListComponent = () => {
    return filteredSuggestions.length ? (
      <ul class='suggestions'>
        {filteredSuggestions.map((suggestion, index) => {
          let className
          // Flag the active suggestion with a class
          if (index === activeSuggestionIndex) {
            className = 'suggestion-active'
          }
          return (
            <li className={className} key={suggestion} onClick={onClick}>
              {suggestion}
            </li>
          )
        })}
      </ul>
    ) : (
      <div class='no-suggestions'>
        <em>No suggestions, you're on your own!</em>
      </div>
    )
  }

  const onClick = e => {
    const value = e.target.innerText
    setLocation({
      ...location,
      [e.target.name]: value
    })

    setFilteredSuggestions([])

    setActiveSuggestionIndex(0)
    setShowSuggestions(false)
  }

  function handleChange (evt) {
    evt.preventDefault()
    const value = evt.target.value

    // Filter our suggestions that don't contain the user's input
    const unLinked = suggestions.filter(
      suggestion => suggestion.toLowerCase().indexOf(value.toLowerCase()) > -1
    )

    setLocation({
      ...location,
      [evt.target.name]: value
    })
    setFilteredSuggestions(unLinked)
    setActiveSuggestionIndex(0)
    setShowSuggestions(true)
  }

  const onClickValueChange = event => {
    const temp = location.from
    location.from = location.to
    location.to = temp
    fromValue.current.value = location.to
    toValue.current.value = location.from
  }

  return (
    <div className='app'>
      <Navbar />
      <form action=''>
        <div className='app__form'>
          <label className='input'>
            <input
              ref={fromValue}
              className='input__field'
              type='text'
              placeholder=' '
              name='from'
              id='from'
              value={location.from}
              onChange={handleChange}
              // onClick={onClick}
            />
            {showSuggestions && input && <SuggestionsListComponent />}

            <span className='input__label'>From</span>
          </label>
          <label className='input'>
            <input
              ref={toValue}
              className='input__field'
              type='text'
              placeholder=' '
              name='to'
              id='to'
              value={location.to}
              onChange={handleChange}
              // onClick={onClick}
            />
            <span className='input__label'>To</span>
          </label>
          <div className='app__verticalButton' onClick={onClickValueChange}>
            <SwapVerticalCircleOutlinedIcon fontSize='large' />
          </div>
        </div>
      </form>
    </div>
  )
}

export default App
