import React, { useState, useEffect, useRef } from 'react'
import SwapVerticalCircleOutlinedIcon from '@material-ui/icons/SwapVerticalCircleOutlined'

import './css/Field.css'

export default function ControllableStates ({ options }) {
  const [location, setLocation] = useState({
    from: '',
    to: ''
  })
  const [suggestions, setSuggestions] = useState([])
  const fromValue = useRef(null)
  const toValue = useRef(null)

  function handleChange (evt) {
    evt.preventDefault()
    const value = evt.target.value
    let suggest = []
    if (value.length > 0) {
      const regex = new RegExp(`^${value}`, 'i')
      suggest = options.sort().filter(v => v.match(regex))
      setSuggestions(suggest)
    } else {
      setSuggestions([])
    }

    setLocation({
      ...location,
      [evt.target.name]: value
    })
  }

  function suggestionSelected (value) {
    setLocation({
      ...location,
      from: value
    })

    setSuggestions([])
  }

  function renderSuggestions () {
    if (suggestions.length === 0) {
      return (
        <ul>
          {options.map((item, key) => (
            <li onClick={() => suggestionSelected(item)}>{item}</li>
          ))}
        </ul>
      )
    } else {
      return (
        <ul>
          {suggestions.map((item, key) => (
            <li onClick={() => suggestionSelected(item)}>{item}</li>
          ))}
        </ul>
      )
    }
  }

  const onClickValueChange = event => {
    const temp = location.from
    location.from = location.to
    location.to = temp
    fromValue.current.value = location.to
    toValue.current.value = location.from
  }

  return (
    <div>
      ;
      <form action=''>
        <div className='app__form'>
          <label className='input'>
            <input
              ref={fromValue}
              className='input__field'
              type='text'
              placeholder=' '
              name='from'
              id='from'
              value={location.from}
              onChange={handleChange}
            />

            <span className='input__label'>From</span>
          </label>
          {renderSuggestions()}
          {/* <label className='input'>
            <input
              ref={toValue}
              className='input__field'
              type='text'
              placeholder=' '
              name='to'
              id='to'
              value={location.to}
              onChange={handleChange}
            />

            <span className='input__label'>To</span>
          </label>
          <div className='app__verticalButton' onClick={onClickValueChange}>
            <SwapVerticalCircleOutlinedIcon fontSize='large' />
          </div> */}
        </div>
      </form>
    </div>
  )
}
