import React, { useState, useEffect, useRef } from 'react'
import Navbar from './Components/Navbar'
import Field from './Components/Field'

import SwapVerticalCircleOutlinedIcon from '@material-ui/icons/SwapVerticalCircleOutlined'

import './App.css'

const options = ['Botanical Garden', 'Pari Chowk', 'SNU']

function App () {
  const [location, setLocation] = useState({
    from: '',
    to: ''
  })
  const [suggestions, setSuggestions] = useState([])
  const fromValue = useRef(null)
  const toValue = useRef(null)

  useEffect(() => {
    // console.log(suggestions)
  }, [suggestions])

  function handleChange (evt) {
    evt.preventDefault()
    const value = evt.target.value
    let s = []
    if (value.length > 0) {
      const regex = new RegExp(`^${value}`, 'i')
      s = options.sort().filter(v => v.match(regex))
      setSuggestions(s)
    }

    let renderSuggestions = () => {
      if (suggestions.length === 0) {
        return (
          <ul>
            {options.map(item => (
              <li>{item}</li>
            ))}
          </ul>
        )
      } else {
      }
    }

    setLocation({
      ...location,
      [evt.target.name]: value
    })
  }

  const onClickValueChange = event => {
    const temp = location.from
    location.from = location.to
    location.to = temp
    fromValue.current.value = location.to
    toValue.current.value = location.from
  }

  return (
    <div className='app'>
      <Navbar />
      <form action=''>
        <div className='app__form'>
          <label className='input'>
            <input
              ref={fromValue}
              className='input__field'
              type='text'
              placeholder=' '
              name='from'
              id='from'
              value={location.from}
              onChange={handleChange}
            />

            <span className='input__label'>From</span>
          </label>
          <label className='input'>
            <input
              ref={toValue}
              className='input__field'
              type='text'
              placeholder=' '
              name='to'
              id='to'
              value={location.to}
              onChange={handleChange}
            />

            <span className='input__label'>To</span>
          </label>
          <div className='app__verticalButton' onClick={onClickValueChange}>
            <SwapVerticalCircleOutlinedIcon fontSize='large' />
          </div>
        </div>
      </form>
    </div>
  )
}

export default App
