import React, { useState, useEffect, useRef } from 'react'
import Navbar from './Components/Navbar'
import Field from './Components/Field'
import TabsForm from './Components/TabsForm'
import Button from './Components/Button'
// import { currentDate, tomorrowDate, dayAfterDate } from './util'
import './App.css'

const options = ['Botanical Garden', 'Pari Chowk', 'SNU']
// const weekday = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat']

function App () {
  const [location, setLocation] = useState({
    from: '',
    to: ''
  })

  const [dates, setDates] = useState({
    date: '',
    day: '',
    month: ''
  })

  const [toggleButton, setToggleButton] = useState(false)

  useEffect(() => {
    if (location && dates) {
      console.log(toggleButton)
    }
  }, [toggleButton])

  return (
    <div className='app'>
      <Navbar />
      <form>
        <Field
          options={options}
          location={location}
          setLocation={setLocation}
        />
        <TabsForm dates={dates} setDates={setDates} />
        <Button toggleButton={toggleButton} setToggleButton={setToggleButton} />
      </form>
    </div>
  )
}

export default App
